DROP TABLE IF EXISTS `weiit_product`;
CREATE TABLE `weiit_product` (
  `product_id` int(11) NOT NULL AUTO_INCREMENT COMMENT '主键、自动增长',
  `product_num` varchar(80) DEFAULT NULL COMMENT '商品编号，每个商品可以拥有自己的商品编号',
  `brand_id` int(11) DEFAULT NULL COMMENT '品牌ID，商品所属什么品牌,暂时预留字段',
  `cate_id_level1` int(11) DEFAULT NULL COMMENT '商品一级分类，保留的目的是为了提高商品筛选查询速度。',
  `cate_id_level2` int(11) DEFAULT NULL COMMENT '商品二级分类，为了提高商品筛选速度。',
  `cate_id_level3` int(11) DEFAULT NULL COMMENT '商品三级分类，为了提高商品筛选速度。',
  `product_name` varchar(150) DEFAULT NULL COMMENT '商品名称',
  `supply_price` decimal(10,2) DEFAULT NULL COMMENT '商品供应价',
  `smarket_price` decimal(10,2) DEFAULT NULL COMMENT '商品市场销售价格，即商品原价。',
  `sale_price` decimal(10,2) DEFAULT NULL COMMENT '商品实际销售价格，即折扣价。',
  `stock` int(11) DEFAULT NULL COMMENT '商品总库存',
  `product_img` varchar(200) DEFAULT NULL COMMENT '商品主图',
  `sale_count` int(11) DEFAULT '0' COMMENT '商品销量',
  `purchase` int(11) DEFAULT NULL COMMENT '限购数，商品可以限购只能购买多少。',
  `spec_config` tinyint(2) DEFAULT '0' COMMENT '商品规格类型设置，0表示统一规格，即没有SKU；1表示多规格，即有多个SKU,默认0，表示统一规格。',
  `attribute_custom` varchar(500) DEFAULT NULL COMMENT '属性自定义，结构如：材料:牛皮|颜色:红色 黑色 白色 蓝色 紫色|上市时间:2016年秋',
  `spec_custom` varchar(500) DEFAULT NULL COMMENT '规格自定义，这里存储的是ID集合，即规格的内容值的ID。',
  `integral` double DEFAULT NULL COMMENT '商品购买后赠送积分。',
  `express_config` tinyint(2) DEFAULT NULL COMMENT '运费方式，默认-1表示统一运费，0表示按照运费设置，运费设置需要根据express_template_id决定。',
  `express_money` decimal(10,2) DEFAULT NULL COMMENT '运费，当express_config为-1时，该字段为商品的运费。',
  `express_template_id` int(11) DEFAULT NULL COMMENT '运费模板id，当express_config为0时，以运费模板价格为准。',
  `weight` decimal(10,2) DEFAULT NULL COMMENT '商品重量，计算商品运费使用， 根据商品模板变化， 按重量是单位为克（g）,按体积时单位为立方厘米(cm)',
  `volume` decimal(10,2) DEFAULT NULL COMMENT '商品体积，计算商品运费使用， 根据商品模板变化， 按体积时单位为立方厘米(cm)',
  `is_on_sale` tinyint(2) DEFAULT '0' COMMENT '是否销售中，默认0表示销售中，-1表示下架。',
  `sort_num` int(11) DEFAULT NULL COMMENT '商品排序',
  `is_disabled` tinyint(2) DEFAULT '0' COMMENT '是否禁用，默认0表示正常，-1表示禁用。',
  `is_deleted` tinyint(2) DEFAULT '0' COMMENT '是否删除，默认0表示正常，-1表示已删除',
  `create_mid` int(11) DEFAULT NULL COMMENT '建创人id',
  `create_time` datetime DEFAULT NULL COMMENT '创建时间',
  `update_mid` int(11) DEFAULT NULL COMMENT '修改人id',
  `update_time` datetime DEFAULT NULL COMMENT '修改时间',
  `demo_id` int(11) DEFAULT NULL COMMENT '行业',
  PRIMARY KEY (`product_id`)
) ENGINE=InnoDB AUTO_INCREMENT=601 DEFAULT CHARSET=utf8 COMMENT='商品表';

