-- Table structure for weiit_product_desc
-- ----------------------------
DROP TABLE IF EXISTS `weiit_product_desc`;
CREATE TABLE `weiit_product_desc` (
  `id` int(11) NOT NULL AUTO_INCREMENT COMMENT '主键、自动增长',
  `product_id` int(11) DEFAULT NULL COMMENT '商品id',
  `product_desc` text COMMENT '商品描述，由于是text类型，是大字段。之所以设计该字段与商品表独立，是为了提高商品表性能，也是为了方便以后商品数据量过大，导致数据迁移时会因为该product_desc是大字段导致迁移成本过大，大字段数据做迁移特别麻烦，所以把商品表的描述独立开来，这样可以先对其他的表的数据做迁移，而大字段数据可以往后迁移，或者分步迁移。',
  `is_deleted` tinyint(2) DEFAULT '0' COMMENT '是否删除，默认0，表示正常；-1表示已删除',
  `create_time` datetime DEFAULT NULL COMMENT '创建时间',
  `update_time` datetime DEFAULT NULL COMMENT '修改时间',
  `demo_id` int(11) DEFAULT NULL COMMENT '行业',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=499 DEFAULT CHARSET=utf8 COMMENT='商品描述表';

