  `groupon_number` int(10) DEFAULT '0' COMMENT '参团人数 ',
  `max_limit` int(10) DEFAULT '0' COMMENT '限购数量，为空表示不限制 ',
  `virtual_groupon` tinyint(4) DEFAULT '0' COMMENT '模拟成团，建议合理开启，能高成团率,为1表示开启 ',
  `head_discount` decimal(10,2) DEFAULT '0.00' COMMENT '团长优惠',
  `state` tinyint(4) DEFAULT '0' COMMENT '-2 未开始 -1：失效，0生效中',
  `create_time` datetime DEFAULT NULL COMMENT '创建时间',
  `update_time` datetime DEFAULT NULL COMMENT '修改时间',
  `is_delete` tinyint(255) DEFAULT '0' COMMENT '-1 已删除 0正常',
  `is_morespec` tinyint(2) DEFAULT '0' COMMENT '是否多规格拼团商品 0 否 1 是 0时拼团价格取该表groupon_price 1 时 根据spec_custom 和product 取 weiit_product_item 表groupon_price',
  `demo_id` int(11) DEFAULT NULL COMMENT '行业',
  PRIMARY KEY (`groupon_id`)
) ENGINE=InnoDB AUTO_INCREMENT=101 DEFAULT CHARSET=utf8 COMMENT='营销-拼团活动表';

