mapper.select:
SELECT ub.bargain_id, ub.bargain_name, ub.end_time, pi.stock, ub.max_price, ub.min_price, wp.product_img, wp.product_name, wp.sale_count FROM weiit_ump_bargain ub LEFT JOIN weiit_product wp ON ub.product_id = wp.product_id LEFT JOIN weiit_product_item pi ON ub.item_id =pi.item_id WHERE wp.is_on_sale=0 AND ub.state=0 and ub.shop_id=#{shop_id}
and ub.bargain_id IN
#{bargain_id}

SELECT ub.bargain_id, ub.min_price, ub.cut_price_min, ub.cut_price_max, wp.product_name, ub.max_price FROM weiit_ump_bargain ub LEFT JOIN weiit_product wp ON ub.product_id = wp.product_id WHERE 1 = 1
AND bargain_id=#{bargain_id}
AND ub.shop_id=#{shop_id}

SELECT ub.order_num, ub.user_id, ub.end_time, ub.state, ub.pay_time, ub.start_time, ub.payment_type, ub.address_id, ub.item_id, wp.product_id, ub.bargain_id, wp.smarket_price, wp.product_img, wp.product_name, wp.sale_price, ub.pay_price, ub.b_order_id,ub.shop_id FROM weiit_ump_bargain_order ub LEFT JOIN weiit_product wp ON ub.product_id=wp.product_id WHERE 1=1
AND b_order_id = #{b_order_id}
AND order_num = #{order_num}

SELECT ub.bargain_id, ub.min_price, ub.cut_price_min, ub.cut_price_max, ub.shop_id, ubl.after_price, ubl.bargain_price, ubl.log_id, ubl.b_order_id FROM weiit_ump_bargain ub LEFT JOIN weiit_ump_bargain_order_log ubl ON ubl.bargain_id = ub.bargain_id WHERE ub.shop_id=#{shop_id}
AND ubl.user_id=#{user_id}
AND ubl.b_order_id=#{b_order_id}
