mapper.select:
SELECT us.groupon_id, us.groupon_id AS validate_id, us.product_id, op.product_name, op.product_img, op.stock, us.groupon_price, us.groupon_name, us.start_time, us.end_time, us.expire_time, us.groupon_number, us.max_limit, us.virtual_groupon, us.head_discount, us.state, us.create_time, us.update_time, us.shop_id FROM weiit_ump_groupon us, weiit_product op where us.product_id=op.product_id
and us.groupon_name like concat('%', #{groupon_name}, '%')
and us.state = #{state}
and us.shop_id=#{shop_id}
order by us.create_time desc

mapper.update:
update weiit_ump_groupon set
state=#{state} ,
product_id = #{product_id}, groupon_price = #{groupon_price}, groupon_name = #{groupon_name}, start_time=#{start_time}, end_time=#{end_time}, expire_time = #{expire_time}, groupon_number = #{groupon_number}, max_limit = #{max_limit}, virtual_groupon = #{virtual_groupon}, head_discount = #{head_discount}, state=#{state},
update_time=now() where groupon_id=#{validate_id}
and shop_id=#{shop_id}

update weiit_ump_groupon_order


		state=#{state},


		pay_time=#{pay_time},


		check_num=#{check_num},


 where  order_num=#{order_num}

 	and shop_id=#{shop_id}

update weiit_ump_groupon_order


		state=#{state}


 where  state=1 AND (g_order_id=#{g_order_id} OR parent_id=#{g_order_id})

 	and shop_id=#{shop_id}

mapper.select:
select gi.product_id, gi.item_id, gi.groupon_price, pi.stock, up.max_limit from weiit_ump_groupon_item gi LEFT JOIN weiit_product_item pi ON gi.item_id = pi.item_id LEFT JOIN weiit_ump_groupon up ON gi.groupon_id = up.groupon_id WHERE gi.is_delete=0
AND gi.product_id = #{product_id}
AND gi.item_id = #{item_id}
AND gi.spec_custom = #{spec_custom}

SELECT ug.groupon_id, ug.product_id, ug.groupon_name, ug.groupon_number, ug.groupon_price, wp.product_img, wp.product_name, wp.sale_count, wp.sale_price FROM weiit_ump_groupon ug LEFT JOIN weiit_product wp ON ug.product_id = wp.product_id WHERE ug.shop_id=#{shop_id} AND ug.is_delete=0
AND groupon_id IN
#{groupon_id}

SELECT * FROM weiit_ump_groupon_order WHERE 1=1
AND g_order_id=#{g_order_id}
AND order_num=#{order_num}
