mapper.select:
SELECT page_id AS validate_id, page_name, page_desc, bgcolor, create_time, update_time, shop_id, page_layout FROM weiit_page WHERE is_deleted = 0
and shop_id = #{shop_id}
and page_id =#{page_id}
and is_default =#{is_default}
and is_default =#{is_default}
order by create_time desc limit 1

mapper.delete:
delete from weiit_page
where  page_id=#{validate_id}

    and shop_id = #{shop_id}
