mapper.select:
SELECT ( SELECT count(1) FROM weiit_order WHERE state = 0 ) no_pay_order_count, ( SELECT count(1) FROM weiit_order WHERE state = 1 ) no_send_order_count, ( SELECT count(1) FROM weiit_order WHERE state = 4 ) after_sale_order_count, ( SELECT count(1) FROM weiit_order WHERE (to_days(create_time)=to_days(now()) or to_days(update_time)=to_days(now()) ) and state=1
and shop_id=#{shop_id}
) today_total, ( SELECT count(1) FROM weiit_order WHERE (to_days(now())-to_days(create_time)<=1 or to_days(now())-to_days(update_time)<=1 ) and state=1
and shop_id=#{shop_id}
) yesterday_total, ( SELECT count(1) FROM weiit_order WHERE (DATE_SUB(CURDATE(), INTERVAL 30 DAY)<=date(create_time) or DATE_SUB(CURDATE(), INTERVAL 30 DAY)<=date(update_time) ) and state=1
and shop_id=#{shop_id}
) month_total from dual

SELECT DAY (create_time) DAY, sum(pay_price) sale_total, count(1) orderCount FROM weiit_order WHERE (state!=0 or state!=-1) and (DATE_SUB(CURDATE(), INTERVAL 30 DAY)<=date(create_time) or DATE_SUB(CURDATE(), INTERVAL 30 DAY)<=date(update_time) )
and temp.shop_id=#{shop_id}
group by day(create_time)

select count(1) sale_total, quarter(create_time) quarter from weiit_order where (state!=0 or state!=-1)
and temp.shop_id=#{shop_id}
group by quarter(create_time)
