mapper.select:
select oo.order_id, oo.order_id as validate_id , oo.order_num, oo.total_price, oo.check_num, oo.pay_price, oo.address_id, oo.user_id, oo.user_name, oo.create_time, oo.update_time, oo.state, oo.order_type, oo.remark, oo.payment_type, oo.express_num, oo.express_type, oo.express_money, oo.consignee, oo.address, oo.phone, oo.express_template_id, oc.express_name, ex.express_code, oo.star_state from weiit_order oo LEFT JOIN weiit_shop_express oc on oo.express_template_id = oc.id LEFT JOIN weiit_init_express_ali ex on oc.express_id = ex.express_id and ex.express_type = 0 where 1=1
and oo.shop_id=#{shop_id}
and oo.user_name like concat('%', #{user_name}, '%')
and oo.order_num like concat('%', #{order_num}, '%')
and exists ( select od.order_id from weiit_order_item od where od.order_id=oo.order_id and od.product_name like concat('%', #{product_name}, '%') )
and oo.state =#{state}
and oo.star_state =#{star_state}
and date(oo.create_time) >= date(#{startTime})
and date(oo.create_time) <= date(#{endTime})
order by oo.create_time desc

mapper.update:
update  weiit_order 
set update_time=now() , 
	state=#{state} 
where 
	order_id=#{validate_id}

mapper.select:
select od.id, od.product_id, od.product_name, od.product_img, od.supply_price, od.smarket_price, od.sale_price, od.spec_content, od.order_id, od.create_time, od.update_time, od.item_id, od.count, (od.count * od.sale_price) AS totalPrice, rd.refund_id, rd.state from weiit_order_item od left join weiit_order_refund rd on od.id = rd.order_item_id where 1=1
and od.order_num =#{validate_id}
and od.order_id = #{order_id}
and od.shop_id=#{shop_id}

select oo.order_id, oo.order_id as validate_id , oo.order_num, oo.total_price, oo.check_num, oo.pay_price, oo.address_id, oo.user_id, oo.user_name, oo.create_time, oo.update_time, oo.state, oo.order_type, oo.remark, oo.payment_type, oo.express_num, oo.express_type, oo.express_money, oo.consignee, oo.address, oo.phone, oo.express_template_id, oc.express_name, ex.express_code, oo.shop_id, (select shop_name from weiit_shop ss where ss.shop_id=oo.shop_id) as shopName from weiit_order oo LEFT JOIN weiit_shop_express oc on oo.express_template_id = oc.id LEFT JOIN weiit_init_express_ali ex on oc.express_id = ex.express_id and ex.express_type = 0 where 1=1
and oo.shop_id=#{shop_id}
and oo.order_id =#{validate_id}
limit 1

mapper.update:
   update  weiit_product 
   set 
	 stock=stock-#{count},
	 sale_count=sale_count+#{count}
   where 
   	 product_id=#{product_id}

   update  weiit_product_item 
   set 
	    stock=stock-#{count}
   where 
   		item_id=#{item_id}
		and product_id =#{product_id}

 	    update weiit_order set update_time = now(), deliver_time = now()

         , express_num=#{express_num} 


         , express_template_id=#{express_template_id} 


         , state=#{state}


         , star_state=#{star_state} 

 	    where order_id=#{validate_id} 

			and shop_id = #{shop_id}

mapper.select:
SELECT ev.evaluate_id, ev.evaluate_id AS validate_id, ev.evaluate_desc, ev.evaluate_imgs, ev.score, ev.evaluate_reply, ev.create_time, ev.update_time, ev.is_show, ev.state, ev.order_id, ev.order_num, ev.order_item_id, ev.user_id, ev.user_name, ev.user_img, it.product_id, it.product_name, it.product_img, it.spec_content FROM weiit_order_evaluate ev, weiit_order_item it where ev.order_item_id=it.id
and ev.shop_id=#{shop_id}
and ev.user_name=#{user_name}
and ev.order_num=#{order_num}
and ev.state =#{state}
and ev.is_show =#{is_show}
and ev.score =#{score}
order by ev.create_time desc

mapper.update:
update weiit_order_evaluate set
state=#{state} ,
evaluate_reply=#{evaluate_reply} ,
is_show=#{is_show} ,
update_time=now() where evaluate_id=#{validate_id}
and shop_id=#{shop_id}

mapper.select:
select ( select count(1) from weiit_order where state=0
and shop_id=#{shop_id}
) no_pay_order_count, ( select count(1) from weiit_order where state=1
and shop_id=#{shop_id}
) no_send_order_count, ( select count(1) from weiit_order_refund where state=0
and shop_id=#{shop_id}
) after_sale_order_count, ( select count(*) from weiit_order where state >0
and shop_id=#{shop_id}
and (to_days(create_time)=to_days(now()) or to_days(update_time)=to_days(now()) ) ) today_count, ( select ifnull(sum(round(pay_price,2)),0) from weiit_order where state >0
and shop_id=#{shop_id}
AND (to_days(create_time)=to_days(now()) or to_days(update_time)=to_days(now()) ) ) today_total, ( select ifnull(sum(round(pay_price,2)),0) from weiit_order where state >0
and shop_id=#{shop_id}
AND (to_days(now())-to_days(create_time)<=1 or to_days(now())-to_days(update_time)<=1 ) ) yesterday_total , ( select count(*) from weiit_order where state >0
and shop_id=#{shop_id}
AND (to_days(now())-to_days(create_time)<=1 or to_days(now())-to_days(update_time)<=1 ) ) yesterday_count from dual
