mapper.select:
		SELECT id, id as validate_id , 
			item_code, item_name, item_value, state, item_desc, shop_id 
		FROM weiit_shop_parameter 
  		where 1=1 

			and shop_id = #{shop_id} 


			and id = #{validate_id} 


			and id in (${validate_ids}) 


			and item_code = #{item_code} 

		order by id asc

		SELECT id, id as validate_id,
			item_code, item_name, item_value, state, item_desc, shop_id 
		FROM weiit_shop_parameter 
  		where  id=#{validate_id} 

			and shop_id = #{shop_id}

		SELECT id, id as validate_id,
			item_code, item_name, item_value, state, item_desc, shop_id
		FROM weiit_shop_parameter
  		where  item_name=#{item_name}

			and shop_id = #{shop_id}

mapper.update:
update weiit_shop_parameter    
set update_time=now() 

	, item_code=#{item_code}


	, item_name=#{item_name} 


	, item_value=#{item_value} 


	, state = #{state} 


	, item_desc = #{item_desc} 

where  id=#{validate_id} 

	and shop_id = #{shop_id}

mapper.delete:
delete  from weiit_shop_parameter  
where id=#{validate_id} 

	and shop_id = #{shop_id}

delete from weiit_shop_parameter  
where id in (${validate_ids}) 

	and shop_id = #{shop_id}

mapper.insert:
insert into weiit_shop_parameter 
( 
	item_code, item_name, item_value, state, item_desc, shop_id ,create_time,update_time
 ) values ( 
 	#{item_code},#{item_name},#{item_value},#{state},#{item_desc},#{shop_id},now(),now()
 )
