mapper.select:
SELECT shipping_id, shipping_name, shipping_config, create_time, update_time, shop_id FROM weiit_shop_shipping WHERE is_deleted = 0
and shipping_id =#{shipping_id}
and shipping_name like concat('%', #{queryName}, '%')
and shop_id = #{shop_id}
order by update_time desc

mapper.update:
	update  weiit_shop_shipping set 

            shipping_name=#{shipping_name},


            shipping_config=#{shipping_config},

		update_time=now() 
	where  shipping_id=#{shipping_id} 

		and  shop_id = #{shop_id}

mapper.select:
SELECT id, areas_ids, areas_names, start_standard, start_fee, add_standard, add_fee, sort_num, create_time, shipping_id, shop_id FROM weiit_shop_shipping_item WHERE 1 = 1
and shipping_id in
#{item.shipping_id}
and shipping_id =#{shipping_id}
and shop_id = #{shop_id}
order by create_time desc , sort_num asc
