mapper.update:
UPDATE weiit_page SET page_name = #{page_name}, page_desc = #{page_desc}, bgcolor = #{bgcolor}, update_time = now(),
page_cover=#{page_cover},
page_layout=#{page_layout} WHERE page_id = #{validate_id}
and shop_id = #{shop_id}

update weiit_page
SET is_deleted=-1
where  page_id=#{validate_id}

    and shop_id = #{shop_id}

update weiit_page
SET is_default=1,update_time=now()
where  page_id=#{validate_id}

    and shop_id = #{shop_id}
