mapper.select:
SELECT
	id as validate_id,
	a.*
FROM
	weiit_weixin_public_media_article a
WHERE
	media_id=#{media_id}
	and id=#{validate_id}
	and authorizer_id=#{authorizer_id}

mapper.delete:
DELETE FROM
	weiit_weixin_public_media_article 
WHERE media_id=#{media_id}
and id=#{validate_id}
and authorizer_id=#{authorizer_id}
