mapper.select:
SELECT t.record_id, t.state, t.create_time, t.send_time, t.action_type, t.send_content, t.group_tag, ( SELECT tt.image_url FROM weiit_weixin_public_media_image tt WHERE tt.authorizer_id=#{authorizer_id} and tt.media_id = t.send_content ) AS image_url, ( SELECT tt.title FROM weiit_weixin_public_media_article tt WHERE tt.authorizer_id=#{authorizer_id} and tt.media_id = t.send_content ) AS title, ( SELECT tt.cover_img FROM weiit_weixin_public_media_article tt WHERE tt.authorizer_id=#{authorizer_id} and tt.media_id = t.send_content ) AS cover_img, ( SELECT count(1) FROM weiit_weixin_public_masssend_user tt WHERE tt.record_id = t.record_id ) AS allCount, ( SELECT count(1) FROM weiit_weixin_public_masssend_user tt WHERE tt.record_id = t.record_id AND tt.state = 0 ) AS successCount FROM weiit_weixin_public_masssend_record t WHERE 1 = 1
t.record_id=#{record_id}
and t.authorizer_id=#{authorizer_id}
order by t.send_time desc

mapper.update:
update weiit_weixin_public_masssend_user set state=#{state}, reason=#{reason}, create_time=now() where 1=1
and record_id = #{record_id}
and authorizer_id=#{authorizer_id}

mapper.select:
SELECT
	user_id,  user_name,  wx_open_id 
FROM
	weiit_user us
WHERE open_id_type=1 

	and region = #{region_name} 


	and user_sex = #{user_sex} 


	and exists ( SELECT id from weiit_user_sign_log where user_id=us.user_id )


	and shop_id = #{shop_id}

select t.id, t.record_id, t.user_id, t.state, t.create_time, t.reason, tt.user_name, tt.user_img, tt.wx_open_id FROM weiit_weixin_public_masssend_user t , weiit_user tt WHERE t.user_id=tt.user_id and t.authorizer_id=#{authorizer_id}
and t.record_id=#{record_id}
and tt.user_name like concat('%', #{user_name}, '%')
order by tt.user_id desc
