mapper.update:
        update weiit_shop 
        set 
	        update_time=now(),
	 		shop_name=#{shop_name} 
        where  shop_id=#{shop_id} and mrch_id=#{validate_id} 

 			and shop_id in (select s.shop_id from weiit_shop s,weiit_merchant m where s.mrch_id=m.mrch_id and m.create_manager_id=#{manager_id} )

        update weiit_shop_certification 
        set 
	        update_time=now() ,
	 		state=#{state}
        where  shop_id=#{shop_id}  

 			and shop_id in (select s.shop_id from weiit_shop s,weiit_merchant m where s.mrch_id=m.mrch_id and m.create_manager_id=#{manager_id} )

mapper.select:
SELECT mrch_id, mrch_id as validate_id, account, password, description, type, locked, state, is_deleted, version_num, version_begin_time, version_expire_time, create_manager_id, update_manager_id, create_time, update_time FROM weiit_merchant where is_deleted=0 and account=#{account}
and create_manager_id=#{manager_id}
limit 1

mapper.update:
        update weiit_merchant 
        set
	        update_manager_id= #{manager_id} ,
	        update_time=now(), 
	 		locked=#{locked} 
        where  mrch_id=#{validate_id}  

            and create_manager_id=#{manager_id}

mapper.delete:
		delete from weiit_user_suggest  
		where id=#{validate_id} 

 			and shop_id in (select s.shop_id from weiit_shop s,weiit_merchant m where s.mrch_id=m.mrch_id and m.create_manager_id=#{manager_id} )
